import React from 'react';
import useFormState from '../hooks/FormState';
import useFormValidation from '../hooks/Validation';

const JobApplicationForm = () => {
  const [formData, handleChange, setFormData] = useFormState({
    fullName: '',
    email: '',
    phoneNumber: '',
    applyingForPosition: '',
    relevantExperience: '',
    portfolioURL: '',
    managementExperience: '',
    additionalSkills: [],
    preferredInterviewTime: '',
  });

  const errors = useFormValidation(formData);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (Object.keys(errors).length === 0) {
      // Handle form submission logic (e.g., API call or further processing)
      console.log('Form data:', formData);
      // Optionally, display a summary of the entered data
      alert('Form submitted successfully!');
    } else {
      alert('Please fix the errors in the form.');
    }
  };

  return (
    <div className="max-w-lg mx-auto bg-white shadow-md p-8 my-10 rounded-lg">
      <h2 className="text-2xl font-bold mb-8 text-center">Job Application Form</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-gray-700">Full Name</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            className="form-input mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
          />
          {errors.fullName && <p className="text-red-500">{errors.fullName}</p>}
        </div>

        <div>
          <label className="block text-gray-700">Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="form-input mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
          />
          {errors.email && <p className="text-red-500">{errors.email}</p>}
        </div>

        <div>
          <label className="block text-gray-700">Phone Number</label>
          <input
            type="tel"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
            className="form-input mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
          />
          {errors.phoneNumber && <p className="text-red-500">{errors.phoneNumber}</p>}
        </div>

        <div>
          <label className="block text-gray-700">Applying for Position</label>
          <select
            name="applyingForPosition"
            value={formData.applyingForPosition}
            onChange={handleChange}
            className="form-select mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
          >
            <option value="">Select...</option>
            <option value="Developer">Developer</option>
            <option value="Designer">Designer</option>
            <option value="Manager">Manager</option>
          </select>
          {errors.applyingForPosition && <p className="text-red-500">{errors.applyingForPosition}</p>}
        </div>

        {formData.applyingForPosition === 'Developer' || formData.applyingForPosition === 'Designer' ? (
          <div>
            <label className="block text-gray-700">Relevant Experience (years)</label>
            <input
              type="number"
              name="relevantExperience"
              value={formData.relevantExperience}
              onChange={handleChange}
              className="form-input mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
            />
            {errors.relevantExperience && <p className="text-red-500">{errors.relevantExperience}</p>}
          </div>
        ) : null}

        {formData.applyingForPosition === 'Designer' ? (
          <div>
            <label className="block text-gray-700">Portfolio URL</label>
            <input
              type="text"
              name="portfolioURL"
              value={formData.portfolioURL}
              onChange={handleChange}
              className="form-input mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
            />
            {errors.portfolioURL && <p className="text-red-500">{errors.portfolioURL}</p>}
          </div>
        ) : null}

        {formData.applyingForPosition === 'Manager' ? (
          <div>
            <label className="block text-gray-700">Management Experience</label>
            <input
              type="text"
              name="managementExperience"
              value={formData.managementExperience}
              onChange={handleChange}
              className="form-input mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
            />
            {errors.managementExperience && <p className="text-red-500">{errors.managementExperience}</p>}
          </div>
        ) : null}

        <div>
          <label className="block text-gray-700">Additional Skills</label>
          <div className="mt-2 space-y-2">
            <label className="inline-flex items-center">
              <input
                type="checkbox"
                name="additionalSkills"
                value="JavaScript"
                onChange={handleChange}
                checked={formData.additionalSkills.includes('JavaScript')}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span className="ml-2 text-gray-700">JavaScript</span>
            </label>
            <label className="inline-flex items-center">
              <input
                type="checkbox"
                name="additionalSkills"
                value="CSS"
                onChange={handleChange}
                checked={formData.additionalSkills.includes('CSS')}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span className="ml-2 text-gray-700">CSS</span>
            </label>
            <label className="inline-flex items-center">
              <input
                type="checkbox"
                name="additionalSkills"
                value="Python"
                onChange={handleChange}
                checked={formData.additionalSkills.includes('Python')}
                className="form-checkbox h-5 w-5 text-blue-600"
              />
              <span className="ml-2 text-gray-700">Python</span>
            </label>
            {/* Add other skills as needed */}
          </div>
          {errors.additionalSkills && <p className="text-red-500">{errors.additionalSkills}</p>}
        </div>

        <div>
          <label className="block text-gray-700">Preferred Interview Time</label>
          <input
            type="datetime-local"
            name="preferredInterviewTime"
            value={formData.preferredInterviewTime}
            onChange={handleChange}
            className="form-input mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50"
          />
          {errors.preferredInterviewTime && <p className="text-red-500">{errors.preferredInterviewTime}</p>}
        </div>

        <div className="mt-6">
          <button
            type="submit"
            className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:ring focus:ring-blue-500 focus:ring-opacity-50"
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default JobApplicationForm;
