import React from 'react';
import useStateForm from './useStateForm';
import useFormValidation from './useValidation';

const JobApplicationForm = () => {
  const { values, handleChange, handleCheckboxChange, handleDateChange, handleDropdownChange, setSubmitted } = useStateForm();

  const { errors, validate } = useFormValidation();

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValid = validate(values);
    if (isValid) {
      setSubmitted(true);
    }
  };

  return (
    <form onSubmit={handleSubmit} noValidate>
      {/* Full Name */}
      <div>
        <label htmlFor="fullName">Full Name:</label>
        <input
          type="text"
          id="fullName"
          name="fullName"
          value={values.fullName}
          onChange={handleChange}
          required
        />
        {errors.fullName && <p>{errors.fullName}</p>}
      </div>

      {/* Email */}
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={values.email}
          onChange={handleChange}
          required
        />
        {errors.email && <p>{errors.email}</p>}
      </div>

      {/* Phone Number */}
      <div>
        <label htmlFor="phone">Phone Number:</label>
        <input
          type="tel"
          id="phone"
          name="phone"
          value={values.phone}
          onChange={handleChange}
          required
        />
        {errors.phone && <p>{errors.phone}</p>}
      </div>

      {/* Applying for Position (Dropdown) */}
      <div>
        <label htmlFor="position">Applying for Position:</label>
        <select
          id="position"
          name="position"
          value={values.position}
          onChange={handleDropdownChange}
          required
        >
          <option value="">Select...</option>
          <option value="Developer">Developer</option>
          <option value="Designer">Designer</option>
          <option value="Manager">Manager</option>
        </select>
        {errors.position && <p>{errors.position}</p>}
      </div>

      {/* Conditional Fields based on position */}
      {values.position === 'Developer' || values.position === 'Designer' ? (
        <div>
          <label htmlFor="experience">Relevant Experience (years):</label>
          <input
            type="number"
            id="experience"
            name="experience"
            value={values.experience}
            onChange={handleChange}
            required={values.position === 'Developer' || values.position === 'Designer'}
            min="1"
          />
          {errors.experience && <p>{errors.experience}</p>}
        </div>
      ) : null}

      {values.position === 'Designer' ? (
        <div>
          <label htmlFor="portfolio">Portfolio URL:</label>
          <input
            type="text"
            id="portfolio"
            name="portfolio"
            value={values.portfolio}
            onChange={handleChange}
            required={values.position === 'Designer'}
          />
          {errors.portfolio && <p>{errors.portfolio}</p>}
        </div>
      ) : null}

      {values.position === 'Manager' ? (
        <div>
          <label htmlFor="managementExperience">Management Experience:</label>
          <input
            type="text"
            id="managementExperience"
            name="managementExperience"
            value={values.managementExperience}
            onChange={handleChange}
            required={values.position === 'Manager'}
          />
          {errors.managementExperience && <p>{errors.managementExperience}</p>}
        </div>
      ) : null}

      {/* Additional Skills (checkboxes) */}
      <div>
        <label>Additional Skills:</label>
        <div>
          <label>
            <input
              type="checkbox"
              name="skills"
              value="JavaScript"
              checked={values.skills.includes('JavaScript')}
              onChange={handleCheckboxChange}
            />{' '}
            JavaScript
          </label>
          {/* Add more checkboxes for other skills */}
        </div>
        {errors.skills && <p>{errors.skills}</p>}
      </div>

      {/* Preferred Interview Time */}
      <div>
        <label htmlFor="interviewTime">Preferred Interview Time:</label>
        <input
          type="datetime-local"
          id="interviewTime"
          name="interviewTime"
          value={values.interviewTime}
          onChange={handleDateChange}
          required
        />
        {errors.interviewTime && <p>{errors.interviewTime}</p>}
      </div>

      {/* Submit Button */}
      <button type="submit">Submit</button>

      {/* Display summary of entered data on successful submission */}
      {values.submitted && (
        <div>
          <h2>Application Summary</h2>
          <p><strong>Full Name:</strong> {values.fullName}</p>
          <p><strong>Email:</strong> {values.email}</p>
          <p><strong>Phone Number:</strong> {values.phone}</p>
          <p><strong>Position:</strong> {values.position}</p>
          {/* Display additional fields based on position */}
          {values.position === 'Developer' || values.position === 'Designer' ? (
            <p><strong>Relevant Experience:</strong> {values.experience} years</p>
          ) : null}
          {values.position === 'Designer' ? (
            <p><strong>Portfolio URL:</strong> {values.portfolio}</p>
          ) : null}
          {values.position === 'Manager' ? (
            <p><strong>Management Experience:</strong> {values.managementExperience}</p>
          ) : null}
          <p><strong>Additional Skills:</strong> {values.skills.join(', ')}</p>
          <p><strong>Preferred Interview Time:</strong> {new Date(values.interviewTime).toLocaleString()}</p>
        </div>
      )}
    </form>
  );
};

export default JobApplicationForm;
