import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import Avatar from '@mui/material/Avatar';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { LocalizationProvider, DateTimePicker } from '@mui/x-date-pickers';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import dayjs from 'dayjs';
import useFormState from '../hooks/useFormState';
import useValidation from '../hooks/useValidation';
import Button from '@mui/material/Button';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import MenuItem from '@mui/material/MenuItem';
import Popover from '@mui/material/Popover';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import { Checkbox } from '@mui/material';



const defaultTheme = createTheme();

export default function JobApplicationForm() {

  const skillOptions = ['JavaScript', 'CSS', 'Python'];

  const initialValues = {
    fullName: '',
    email: '',
    phone: '',
    position: '',
    experience: '',
    portfolio: '',
    managementExperience: '',
    skills: [],
    interviewTime: dayjs(), // Initializing with current date and time
    selectedSkills: [],
  };

  const { values, handleChange, handleSkillChange,  setValues } = useFormState(initialValues);
  const { errors, validate } = useValidation();
  const navigate = useNavigate();

  const [customSkill, setCustomSkill] = useState('');
  const [anchorEl, setAnchorEl] = useState(null);

  const handleOpenSkillsMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleCloseSkillsMenu = () => {
    setAnchorEl(null);
  };

  const handleSkillToggle = (skill) => () => {
    const currentIndex = values.skills.indexOf(skill);
    const newSkills = [...values.skills];

    if (currentIndex === -1) {
      newSkills.push(skill);
    } else {
      newSkills.splice(currentIndex, 1);
    }

    setValues({ ...values, skills: newSkills });
  };


  const handleDateTimeChange = (value) => {
    setValues({
      ...values,
      interviewTime: value,
    });
  };


  const handleAddSkill = () => {
    if (customSkill && !values.skills.includes(customSkill)) {
      handleSkillChange([...values.skills, customSkill]);
      setCustomSkill('');
    }
  };

  const handleDeleteSkill = (skillToDelete) => () => {
    handleSkillChange(values.skills.filter((skill) => skill !== skillToDelete));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (validate(values)) {
      navigate('/submitted', { state: { ...values, interviewTime: values.interviewTime.toISOString() } });
    }
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Grid container component="main" sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: 'url(https://picsum.photos/1200/800)',
            backgroundRepeat: 'no-repeat',
            backgroundColor: (t) =>
              t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Job Application
            </Typography>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1, width: '100%' }}>
              {/* Full Name */}
              <TextField
                margin="normal"
                required
                fullWidth
                id="fullName"
                label="Full Name"
                name="fullName"
                value={values.fullName}
                onChange={handleChange}
                autoFocus
              />
              {errors.fullName && (
                <Typography color="error">{errors.fullName}</Typography>
              )}
              {/* Email Address */}
              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                value={values.email}
                onChange={handleChange}
              />
              {errors.email && (
                <Typography color="error">{errors.email}</Typography>
              )}
              {/* Phone Number */}
              <TextField
                margin="normal"
                required
                fullWidth
                id="phone"
                label="Phone Number"
                name="phone"
                autoComplete="tel"
                value={values.phone}
                onChange={handleChange}
              />
              {errors.phone && (
                <Typography color="error">{errors.phone}</Typography>
              )}
              {/* Applying for Position (Dropdown) */}
              <TextField
                select
                margin="normal"
                required
                fullWidth
                id="position"
                label="Applying for Position"
                name="position"
                value={values.position}
                onChange={handleChange}
                SelectProps={{
                  native: true,
                }}
              >
                <option></option>
                <option value="Developer">Developer</option>
                <option value="Designer">Designer</option>
                <option value="Manager">Manager</option>
              </TextField>
              {errors.position && (
                <Typography color="error">{errors.position}</Typography>
              )}
              {/* Relevant Experience (visible if Developer or Designer) */}
              {(values.position === 'Developer' || values.position === 'Designer') && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  id="experience"
                  label="Relevant Experience (years)"
                  name="experience"
                  type="number"
                  value={values.experience}
                  onChange={handleChange}
                  error={!!errors.experience}
                  helperText={errors.experience}
                  inputProps={{ min: 1 }}
                />
              )}
              {/* Portfolio URL (visible if Designer) */}
              {values.position === 'Designer' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  id="portfolio"
                  label="Portfolio URL"
                  name="portfolio"
                  value={values.portfolio}
                  onChange={handleChange}
                  error={!!errors.portfolio}
                  helperText={errors.portfolio}
                />
              )}
              {/* Management Experience (visible if Manager) */}
              {values.position === 'Manager' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  id="managementExperience"
                  label="Management Experience"
                  name="managementExperience"
                  value={values.managementExperience}
                  onChange={handleChange}
                  error={!!errors.managementExperience}
                  helperText={errors.managementExperience}
                />
              )}
              {/* Additional Skills (checkboxes) */}
              <TextField
                select
                margin="normal"
                fullWidth
                id="selectedSkills"
                label="Select Skills"
                name="selectedSkills"
                SelectProps={{
                  multiple: true,
                  value: values.selectedSkills,
                  onChange: (e) => setValues({ ...values, selectedSkills: e.target.value }),
                  renderValue: (selected) => (
                    <Stack direction="row" spacing={1}>
                      {selected.map((skill) => (
                        <Chip key={skill} label={skill} onDelete={handleDeleteSkill(skill)} />
                      ))}
                    </Stack>
                  ),
                }}
              >
                {skillOptions.map((option) => (
                  <MenuItem key={option} value={option}>
                    {option}
                  </MenuItem>
                ))}
              </TextField>
              {/* Custom Skill Input */}
              <TextField
                margin="normal"
                fullWidth
                id="customSkill"
                label="Add Custom Skill"
                value={values.customSkill}
                onChange={(e) => setValues({ ...values, customSkill: e.target.value })}
              />
              {/* Add Custom Skill Button */}
              <Button
                variant="contained"
                color="primary"
                onClick={handleAddSkill}
                sx={{ mt: 2, mb: 2 }}
              >
                Add Skill
              </Button>
              {/* Display Selected Skills */}
              <Stack direction="row" spacing={1}>
                {values.selectedSkills.map((skill) => (
                  <Chip
                    key={skill}
                    label={skill}
                    onDelete={handleDeleteSkill(skill)}
                  />
                ))}
              </Stack>
              {errors.skills && (
                <Typography color="error">{errors.skills}</Typography>
              )}
              {/* Preferred Interview Time */}
              <br />
              < br/>
              <LocalizationProvider dateAdapter={AdapterDayjs}>
                <DateTimePicker
                  label="Preferred Interview Time"
                  value={values.interviewTime}
                  onChange={handleDateTimeChange}
                  renderInput={(params) => <TextField {...params} fullWidth margin="normal" required />}
                />
              </LocalizationProvider>
              {errors.interviewTime && (
                <Typography color="error">{errors.interviewTime}</Typography>
              )}
              {/* Submit Button */}
              <div
                className="relative px-5 py-3 overflow-hidden font-medium text-gray-600 bg-gray-100 border border-gray-100 rounded-lg shadow-inner group"
                style={{
                  marginTop: "20px",
                  textAlign: "center",
                  position: "relative",
                  zIndex: 1,
                }}
                onClick={handleSubmit}
              >
                <span className="absolute top-0 left-0 w-0 h-0 transition-all duration-200 border-t-2 border-gray-600 group-hover:w-full ease"></span>
                <span className="absolute bottom-0 right-0 w-0 h-0 transition-all duration-200 border-b-2 border-gray-600 group-hover:w-full ease"></span>
                <span className="absolute top-0 left-0 w-full h-0 transition-all duration-300 delay-200 bg-gray-600 group-hover:h-full ease"></span>
                <span className="absolute bottom-0 left-0 w-full h-0 transition-all duration-300 delay-200 bg-gray-600 group-hover:h-full ease"></span>
                <span className="absolute inset-0 w-full h-full duration-300 delay-300 bg-gray-900 opacity-0 group-hover:opacity-100"></span>
                <span
                  className="relative transition-colors duration-300 delay-200 group-hover:text-white ease"
                  style={{ zIndex: 2 }}
                >
                  Register
                </span>
              </div>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
}
