import { useState } from 'react';

const ValidateF = (formData) => {
  const [touchedFields, setTouchedFields] = useState({
    fullName: false,
    email: false,
    phoneNumber: false,
    applyingForPosition: false,
    relevantExperience: false,
    portfolioURL: false,
    managementExperience: false,
    additionalSkills: false,
    preferredInterviewDate: false,
    preferredInterviewTime: false,
  });

  const validateForm = () => {
    const {
      fullName,
      email,
      phoneNumber,
      applyingForPosition,
      additionalSkills,
      relevantExperience,
      portfolioURL,
      managementExperience,
      preferredInterviewDate,
      preferredInterviewTime,
    } = formData;

    // Check if at least one additional skill is selected
    const isSkillsValid = Object.values(additionalSkills).some((skill) => skill);

    // Validation rules based on applyingForPosition
    if (applyingForPosition === 'Developer' || applyingForPosition === 'Designer') {
      return (
        fullName.trim() !== '' &&
        isValidEmail(email) &&
        phoneNumber.trim() !== '' &&
        isSkillsValid &&
        relevantExperience.trim() !== ''
      );
    } else if (applyingForPosition === 'Manager') {
      return (
        fullName.trim() !== '' &&
        isValidEmail(email) &&
        phoneNumber.trim() !== '' &&
        isSkillsValid &&
        managementExperience.trim() !== ''
      );
    } else {
      // Default case for other positions (if any)
      return (
        fullName.trim() !== '' &&
        isValidEmail(email) &&
        phoneNumber.trim() !== '' &&
        isSkillsValid
      );
    }
  };

  const isValidEmail = (email) => {
    // Basic email validation using regex
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validateField = (fieldName) => {
    setTouchedFields({
      ...touchedFields,
      [fieldName]: true,
    });
  };

  return { validateForm, validateField, touchedFields };
};

export default useJobApplicationValidation;
