import React from "react";
import { useLocation } from "react-router-dom";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Paper from "@mui/material/Paper";
import Background from "../components/Background";

const SubmitPage = () => {
  const location = useLocation();
  const { state } = location;

  return (
    <Paper elevation={3} style={{ padding: 16, margin: "auto", maxWidth: 600 }}>
      <Box
        sx={{
          my: 8,
          mx: 4,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography component="h1" variant="h5">
          Submitted Details
        </Typography>
        <Typography variant="body1" gutterBottom>
          Name: {state.fullName}
        </Typography>
        <Typography variant="body1" gutterBottom>
          Email: {state.email}
        </Typography>
        <Typography variant="body1" gutterBottom>
          Phone Number: {state.phone}
        </Typography>
        <Typography variant="body1" gutterBottom>
          Position: {state.position}
        </Typography>
        {(state.position === "Developer" || state.position === "Designer" ) && (
          <Typography variant="body1" gutterBottom>
            Experience: {state.experience}
          </Typography>
        )}
           {state.position === "Designer" && (
          <Typography variant="body1" gutterBottom>
            Portfolio URL: {state.portfolio}
          </Typography>
        )}
        {state.position === "Manager" && (
          <Typography variant="body1" gutterBottom>
            Managerial Experience: {state.managementExperience}
          </Typography>
        )}
        <Typography variant="body1" gutterBottom>
          Interview Date and Time: {state.interviewTime}
        </Typography>
      </Box>
    </Paper>
  );
};

export default SubmitPage;
