import React from 'react';
import useFormState from '../hooks/FormState';
import useFormValidation from '../hooks/Validation';
 // Assuming you have some basic styles

const JobApplicationForm = () => {
  const [formData, handleChange, setFormData] = useFormState({
    fullName: '',
    email: '',
    phoneNumber: '',
    applyingForPosition: '',
    relevantExperience: '',
    portfolioURL: '',
    managementExperience: '',
    additionalSkills: [],
    preferredInterviewTime: ''
  });

  const errors = useFormValidation(formData);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (Object.keys(errors).length === 0) {
      // Handle form submission logic (e.g., API call or further processing)
      console.log('Form data:', formData);
      // Optionally, display a summary of the entered data
      alert('Form submitted successfully!');
    } else {
      alert('Please fix the errors in the form.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Full Name</label>
        <input
          type="text"
          name="fullName"
          value={formData.fullName}
          onChange={handleChange}
        />
        {errors.fullName && <p className="error">{errors.fullName}</p>}
      </div>

      <div>
        <label>Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
        />
        {errors.email && <p className="error">{errors.email}</p>}
      </div>

      <div>
        <label>Phone Number</label>
        <input
          type="tel"
          name="phoneNumber"
          value={formData.phoneNumber}
          onChange={handleChange}
        />
        {errors.phoneNumber && <p className="error">{errors.phoneNumber}</p>}
      </div>

      <div>
        <label>Applying for Position</label>
        <select
          name="applyingForPosition"
          value={formData.applyingForPosition}
          onChange={handleChange}
        >
          <option value="">Select...</option>
          <option value="Developer">Developer</option>
          <option value="Designer">Designer</option>
          <option value="Manager">Manager</option>
        </select>
        {errors.applyingForPosition && <p className="error">{errors.applyingForPosition}</p>}
      </div>

      {(formData.applyingForPosition === 'Developer' || formData.applyingForPosition === 'Designer') && (
        <div>
          <label>Relevant Experience (years)</label>
          <input
            type="number"
            name="relevantExperience"
            value={formData.relevantExperience}
            onChange={handleChange}
          />
          {errors.relevantExperience && <p className="error">{errors.relevantExperience}</p>}
        </div>
      )}

      {formData.applyingForPosition === 'Designer' && (
        <div>
          <label>Portfolio URL</label>
          <input
            type="text"
            name="portfolioURL"
            value={formData.portfolioURL}
            onChange={handleChange}
          />
          {errors.portfolioURL && <p className="error">{errors.portfolioURL}</p>}
        </div>
      )}

      {formData.applyingForPosition === 'Manager' && (
        <div>
          <label>Management Experience</label>
          <input
            type="text"
            name="managementExperience"
            value={formData.managementExperience}
            onChange={handleChange}
          />
          {errors.managementExperience && <p className="error">{errors.managementExperience}</p>}
        </div>
      )}

      <div>
        <label>Additional Skills</label>
        <div>
          <label>
            <input
              type="checkbox"
              name="additionalSkills"
              value="JavaScript"
              onChange={handleChange}
              checked={formData.additionalSkills.includes('JavaScript')}
            />
            JavaScript
          </label>
          <label>
            <input
              type="checkbox"
              name="additionalSkills"
              value="CSS"
              onChange={handleChange}
              checked={formData.additionalSkills.includes('CSS')}
            />
            CSS
          </label>
          <label>
            <input
              type="checkbox"
              name="additionalSkills"
              value="Python"
              onChange={handleChange}
              checked={formData.additionalSkills.includes('Python')}
            />
            Python
          </label>
          {/* Add other skills as needed */}
        </div>
        {errors.additionalSkills && <p className="error">{errors.additionalSkills}</p>}
      </div>

      <div>
        <label>Preferred Interview Time</label>
        <input
          type="datetime-local"
          name="preferredInterviewTime"
          value={formData.preferredInterviewTime}
          onChange={handleChange}
        />
        {errors.preferredInterviewTime && <p className="error">{errors.preferredInterviewTime}</p>}
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default JobApplicationForm;
