import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Avatar from '@mui/material/Avatar';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import Checkbox from '@mui/material/Checkbox';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import DatePicker from '@mui/lab/DatePicker';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import TimePicker from '@mui/lab/TimePicker';

const defaultTheme = createTheme();

const JobApplicationForm = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    applyingForPosition: '',
    relevantExperience: '',
    portfolioURL: '',
    managementExperience: '',
    additionalSkills: {
      JavaScript: false,
      CSS: false,
      Python: false,
      Java: false,
      Ruby: false,
    },
    preferredInterviewDate: null,
    preferredInterviewTime: null,
  });

  const [touchedFields, setTouchedFields] = useState({
    fullName: false,
    email: false,
    phoneNumber: false,
    applyingForPosition: false,
    relevantExperience: false,
    portfolioURL: false,
    managementExperience: false,
    additionalSkills: false,
    preferredInterviewDate: false,
    preferredInterviewTime: false,
  });

  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;

    if (type === 'checkbox') {
      setFormData({
        ...formData,
        additionalSkills: {
          ...formData.additionalSkills,
          [name]: checked,
        },
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleDateChange = (field, newValue) => {
    setFormData({
      ...formData,
      [field]: newValue,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Mark all fields as touched
    setTouchedFields({
      fullName: true,
      email: true,
      phoneNumber: true,
      applyingForPosition: true,
      relevantExperience: true,
      portfolioURL: true,
      managementExperience: true,
      additionalSkills: true,
      preferredInterviewDate: true,
      preferredInterviewTime: true,
    });

    // Validate the form
    if (validateForm()) {
      // Handle form submission logic (e.g., API call or further processing)
      console.log('Form data:', formData);
      alert('Form submitted successfully!');
      // Example navigation after successful form submission
      navigate('/submitted'); // Navigate to a success page or another route
    } else {
      alert('Please fix the errors in the form.');
    }
  };

  const validateForm = () => {
    // Basic validation logic
    const { fullName, email, phoneNumber, applyingForPosition } = formData;

    return (
      fullName.trim() !== '' &&
      email.trim() !== '' &&
      isValidEmail(email) &&
      phoneNumber.trim() !== '' &&
      applyingForPosition.trim() !== ''
    );
  };

  const isValidEmail = (email) => {
    // Basic email validation using regex
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const backgroundImageUrl = 'https://picsum.photos/1200/800';

  return (
    <ThemeProvider theme={defaultTheme}>
      <div className="absolute inset-0 -z-10 h-full w-full items-center px-5 py-24 [background:radial-gradient(125%_125%_at_50%_10%,#000_40%,#63e_100%)]"></div>
      <Grid container component="main" sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: `url(${backgroundImageUrl})`,
            backgroundRepeat: 'no-repeat',
            backgroundColor: (t) =>
              t.palette.mode === 'light'
                ? t.palette.grey[50]
                : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Job Application
            </Typography>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="fullName"
                label="Full Name"
                name="fullName"
                autoComplete="fullName"
                value={formData.fullName}
                onChange={handleChange}
                error={touchedFields.fullName && formData.fullName.trim() === ''}
                helperText={touchedFields.fullName && formData.fullName.trim() === '' && 'Full Name is required'}
                autoFocus
              />

              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                value={formData.email}
                onChange={handleChange}
                error={
                  touchedFields.email &&
                  (formData.email.trim() === '' || !isValidEmail(formData.email))
                }
                helperText={
                  touchedFields.email &&
                  (formData.email.trim() === ''
                    ? 'Email is required'
                    : !isValidEmail(formData.email) && 'Invalid email format')
                }
              />

              <TextField
                margin="normal"
                required
                fullWidth
                name="phoneNumber"
                label="Phone Number"
                type="tel"
                id="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
                error={touchedFields.phoneNumber && formData.phoneNumber.trim() === ''}
                helperText={touchedFields.phoneNumber && formData.phoneNumber.trim() === '' && 'Phone Number is required'}
              />

              <FormControl fullWidth margin="normal" required>
                <InputLabel id="applyingForPosition-label">Applying for Position</InputLabel>
                <Select
                  labelId="applyingForPosition-label"
                  id="applyingForPosition"
                  name="applyingForPosition"
                  value={formData.applyingForPosition}
                  onChange={handleChange}
                  error={touchedFields.applyingForPosition && formData.applyingForPosition.trim() === ''}
                >
                  <MenuItem value="Developer">Developer</MenuItem>
                  <MenuItem value="Designer">Designer</MenuItem>
                  <MenuItem value="Manager">Manager</MenuItem>
                </Select>
              </FormControl>

              {formData.applyingForPosition === 'Developer' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="relevantExperience"
                  label="Relevant Experience (Years)"
                  type="number"
                  id="relevantExperience"
                  value={formData.relevantExperience}
                  onChange={handleChange}
                  error={touchedFields.relevantExperience && formData.relevantExperience.trim() === ''}
                  helperText={touchedFields.relevantExperience && formData.relevantExperience.trim() === '' && 'Relevant Experience is required'}
                />
              )}

              {formData.applyingForPosition === 'Designer' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="portfolioURL"
                  label="Portfolio URL"
                  id="portfolioURL"
                  value={formData.portfolioURL}
                  onChange={handleChange}
                  error={touchedFields.portfolioURL && formData.portfolioURL.trim() === ''}
                  helperText={touchedFields.portfolioURL && formData.portfolioURL.trim() === '' && 'Portfolio URL is required'}
                />
              )}

              {formData.applyingForPosition === 'Manager' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="managementExperience"
                  label="Management Experience"
                  id="managementExperience"
                  value={formData.managementExperience}
                  onChange={handleChange}
                  error={touchedFields.managementExperience && formData.managementExperience.trim() === ''}
                  helperText={touchedFields.managementExperience && formData.managementExperience.trim() === '' && 'Management Experience is required'}
                />
              )}

              <FormControl fullWidth margin="normal">
                <InputLabel id="additionalSkills-label">Additional Skills</InputLabel>
                <FormGroup>
                  {Object.keys(formData.additionalSkills).map((skill) => (
                    <FormControlLabel
                      key={skill}
                      control={
                        <Checkbox
                          checked={formData.additionalSkills[skill]}
                          onChange={handleChange}
                          name={skill}
                        />
                      }
                      label={skill}
                    />
                  ))}
                </FormGroup>
              </FormControl>

              <LocalizationProvider dateAdapter={AdapterDateFns}>
                <DatePicker
                  label="Preferred Interview Date"
                  value={formData.preferredInterviewDate}
                  onChange={(newValue) => handleDateChange('preferredInterviewDate', newValue)}
                  renderInput={(params) => <TextField {...params} />}
                  error={touchedFields.preferredInterviewDate && !formData.preferredInterviewDate}
                  helperText={touchedFields.preferredInterviewDate && !formData.preferredInterviewDate && 'Preferred Interview Date is required'}
                />
                <TimePicker
                  label="Preferred Interview Time"
                  value={formData.preferredInterviewTime}
                  onChange={(newValue) => handleDateChange('preferredInterviewTime', newValue)}
                  renderInput={(params) => <TextField {...params} />}
                  error={touchedFields.preferredInterviewTime && !formData.preferredInterviewTime}
                  helperText={touchedFields.preferredInterviewTime && !formData.preferredInterviewTime && 'Preferred Interview Time is required'}
                />
              </LocalizationProvider>

              <div
                className="relative px-5 py-3 overflow-hidden font-medium text-gray-600 bg-gray-100 border border-gray-100 rounded-lg shadow-inner group"
                style={{
                  marginTop: '20px',
                  textAlign: 'center',
                  position: 'relative',
                  zIndex: 1,
                }}
                onClick={handleSubmit}
              >
                <span className="absolute top-0 left-0 w-0 h-0 transition-all duration-200 border-t-2 border-gray-600 group-hover:w-full ease"></span>
                <span className="absolute bottom-0 right-0 w-0 h-0 transition-all duration-200 border-b-2 border-gray-600 group-hover:w-full ease"></span>
                <span className="absolute top-0 left-0 w-full h-0 transition-all duration-300 delay-200 bg-gray-600 group-hover:h-full ease"></span>
                <span className="absolute bottom-0 left-0 w-full h-0 transition-all duration-300 delay-200 bg-gray-600 group-hover:h-full ease"></span>
                <span className="absolute inset-0 w-full h-full duration-300 delay-300 bg-gray-900 opacity-0 group-hover:opacity-100"></span>
                <span
                  className="relative transition-colors duration-300 delay-200 group-hover:text-white ease"
                  style={{ zIndex: 2 }}
                >
                  Submit Application
                </span>
              </div>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

export default JobApplicationForm;
