import React from 'react';
import { useNavigate } from 'react-router-dom';
import Avatar from '@mui/material/Avatar';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import useFormState from '../hooks/FormState';
import Validation from '../hooks/Validation';
import PersonIcon from '@mui/icons-material/Person';
import EmailIcon from '@mui/icons-material/Email';
import DisplaySettingsIcon from '@mui/icons-material/DisplaySettings';
import GroupIcon from '@mui/icons-material/Group';

const defaultTheme = createTheme();

const JobApplicationForm = () => {
  const initialValues = {
    fullName: '',
    email: '',
    phoneNumber: '',
    applyingForPosition: '',
    relevantExperience: '',
    portfolioURL: '',
    managementExperience: '',
    additionalSkills: [],
    preferredInterviewTime: '',
  };

  const { values, handleChange } = useFormState(initialValues);
  const { errors, validate } = Validation();
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent default form submission
    if (validate(values)) {
      navigate('/submitted', { state: { ...values } }); // Redirect to success page with form data
    }
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Grid container component="main" className="min-h-screen bg-gray-100">
        <CssBaseline />
        <Grid item xs={false} sm={4} md={7} className="hidden md:flex bg-cover bg-center" style={{ backgroundImage: 'url("https://source.unsplash.com/random")' }} />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square className="flex items-center justify-center">
          <div className="p-8">
            <Avatar className="mx-auto bg-primary.main">
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5" className="text-center mt-4 mb-8">
              Job Application Form
            </Typography>
            <form onSubmit={handleSubmit} className="space-y-4">
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                id="fullName"
                label="Full Name"
                name="fullName"
                autoComplete="name"
                value={values.fullName}
                onChange={handleChange}
                autoFocus
                InputProps={{
                  startAdornment: <PersonIcon className="text-gray-500" />,
                }}
              />
              {errors.fullName && <Typography color="error">{errors.fullName}</Typography>}

              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                value={values.email}
                onChange={handleChange}
                InputProps={{
                  startAdornment: <EmailIcon className="text-gray-500" />,
                }}
              />
              {errors.email && <Typography color="error">{errors.email}</Typography>}

              <TextField
                variant="outlined"
                margin="normal"
                fullWidth
                id="phoneNumber"
                label="Phone Number"
                name="phoneNumber"
                autoComplete="tel"
                value={values.phoneNumber}
                onChange={handleChange}
                InputProps={{
                  startAdornment: <DisplaySettingsIcon className="text-gray-500" />,
                }}
              />
              {errors.phoneNumber && <Typography color="error">{errors.phoneNumber}</Typography>}

              <FormControl variant="outlined" fullWidth required>
                <InputLabel id="applyingForPosition-label">Applying for Position</InputLabel>
                <Select
                  labelId="applyingForPosition-label"
                  id="applyingForPosition"
                  name="applyingForPosition"
                  value={values.applyingForPosition}
                  onChange={handleChange}
                  label="Applying for Position"
                  startAdornment={<GroupIcon className="text-gray-500" />}
                >
                  <MenuItem value="Developer">Developer</MenuItem>
                  <MenuItem value="Designer">Designer</MenuItem>
                  <MenuItem value="Manager">Manager</MenuItem>
                </Select>
              </FormControl>
              {errors.applyingForPosition && <Typography color="error">{errors.applyingForPosition}</Typography>}

              {values.applyingForPosition === 'Developer' || values.applyingForPosition === 'Designer' ? (
                <TextField
                  variant="outlined"
                  margin="normal"
                  fullWidth
                  id="relevantExperience"
                  label="Relevant Experience (years)"
                  name="relevantExperience"
                  type="number"
                  value={values.relevantExperience}
                  onChange={handleChange}
                  InputProps={{
                    startAdornment: <DisplaySettingsIcon className="text-gray-500" />,
                  }}
                />
              ) : null}
              {errors.relevantExperience && <Typography color="error">{errors.relevantExperience}</Typography>}

              {values.applyingForPosition === 'Designer' ? (
                <TextField
                  variant="outlined"
                  margin="normal"
                  fullWidth
                  id="portfolioURL"
                  label="Portfolio URL"
                  name="portfolioURL"
                  value={values.portfolioURL}
                  onChange={handleChange}
                  InputProps={{
                    startAdornment: <DisplaySettingsIcon className="text-gray-500" />,
                  }}
                />
              ) : null}
              {errors.portfolioURL && <Typography color="error">{errors.portfolioURL}</Typography>}

              {values.applyingForPosition === 'Manager' ? (
                <TextField
                  variant="outlined"
                  margin="normal"
                  fullWidth
                  id="managementExperience"
                  label="Management Experience"
                  name="managementExperience"
                  value={values.managementExperience}
                  onChange={handleChange}
                  InputProps={{
                    startAdornment: <DisplaySettingsIcon className="text-gray-500" />,
                  }}
                />
              ) : null}
              {errors.managementExperience && <Typography color="error">{errors.managementExperience}</Typography>}

              <div className="flex items-center justify-between space-x-4">
                <FormControl variant="outlined" fullWidth required>
                  <InputLabel id="preferredInterviewTime-label">Preferred Interview Time</InputLabel>
                  <TextField
                    variant="outlined"
                    id="preferredInterviewTime"
                    name="preferredInterviewTime"
                    type="datetime-local"
                    value={values.preferredInterviewTime}
                    onChange={handleChange}
                    InputLabelProps={{ shrink: true }}
                    className="w-full"
                  />
                </FormControl>
                {errors.preferredInterviewTime && <Typography color="error">{errors.preferredInterviewTime}</Typography>}
              </div>

              <button
                type="submit"
                className="w-full bg-primary.main hover:bg-primary.dark text-white font-semibold py-2 px-4 rounded focus:outline-none focus:ring focus:ring-primary.dark focus:ring-opacity-50 mt-4"
              >
                Submit
              </button>
            </form>
          </div>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

export default JobApplicationForm;
