// JobApplicationForm.js

import React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import useFormState from '../hooks/useFormState';
import useFormValidation from '../hooks/useValidation';

const defaultTheme = createTheme();

export default function JobApplicationForm() {
  const initialValues = {
    fullName: '',
    email: '',
    phone: '',
    position: '',
    experience: '',
    portfolio: '',
    managementExperience: '',
    skills: [],
    interviewTime: '',
  };

  const { values, handleChange } = useFormState(initialValues);
  const errors = useFormValidation(values);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Check if there are any errors before submitting
    if (Object.keys(errors).length === 0) {
      console.log('Form submitted:', values);
      // Here you can proceed with form submission logic, e.g., sending data to server
    } else {
      console.error('Form has errors:', errors);
      // Handle displaying errors to the user
    }
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Grid container component="main" sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: 'url(https://picsum.photos/1200/800)',
            backgroundRepeat: 'no-repeat',
            backgroundColor: (t) =>
              t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Job Application
            </Typography>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1, width: '100%' }}>
              {/* Full Name */}
              <TextField
                margin="normal"
                required
                fullWidth
                id="fullName"
                label="Full Name"
                name="fullName"
                value={values.fullName}
                onChange={handleChange}
                error={!!errors.fullName}
                helperText={errors.fullName}
                autoFocus
              />

              {/* Email Address */}
              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                value={values.email}
                onChange={handleChange}
                error={!!errors.email}
                helperText={errors.email}
              />

              {/* Phone Number */}
              <TextField
                margin="normal"
                required
                fullWidth
                id="phone"
                label="Phone Number"
                name="phone"
                autoComplete="tel"
                value={values.phone}
                onChange={handleChange}
                error={!!errors.phone}
                helperText={errors.phone}
              />

              {/* Applying for Position (Dropdown) */}
              <TextField
                select
                margin="normal"
                required
                fullWidth
                id="position"
                label="Applying for Position"
                name="position"
                value={values.position}
                onChange={handleChange}
                error={!!errors.position}
                helperText={errors.position}
                SelectProps={{
                  native: true,
                }}
              >
                <option value=""></option>
                <option value="Developer">Developer</option>
                <option value="Designer">Designer</option>
                <option value="Manager">Manager</option>
              </TextField>

              {/* Relevant Experience (visible if Developer or Designer) */}
              {(values.position === 'Developer' || values.position === 'Designer') && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  id="experience"
                  label="Relevant Experience (years)"
                  name="experience"
                  type="number"
                  value={values.experience}
                  onChange={handleChange}
                  error={!!errors.experience}
                  helperText={errors.experience}
                  inputProps={{ min: 1 }}
                />
              )}

              {/* Portfolio URL (visible if Designer) */}
              {values.position === 'Designer' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  id="portfolio"
                  label="Portfolio URL"
                  name="portfolio"
                  value={values.portfolio}
                  onChange={handleChange}
                  error={!!errors.portfolio}
                  helperText={errors.portfolio}
                />
              )}

              {/* Management Experience (visible if Manager) */}
              {values.position === 'Manager' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  id="managementExperience"
                  label="Management Experience"
                  name="managementExperience"
                  value={values.managementExperience}
                  onChange={handleChange}
                  error={!!errors.managementExperience}
                  helperText={errors.managementExperience}
                />
              )}

              {/* Additional Skills (checkboxes) */}
              <FormControlLabel
                control={<Checkbox name="skills" value="JavaScript" color="primary" />}
                label="JavaScript"
              />
              <FormControlLabel
                control={<Checkbox name="skills" value="CSS" color="primary" />}
                label="CSS"
              />
              <FormControlLabel
                control={<Checkbox name="skills" value="Python" color="primary" />}
                label="Python"
              />
              {/* Add more skills as needed */}

              {/* Preferred Interview Time */}
              <TextField
                margin="normal"
                required
                fullWidth
                id="interviewTime"
                label="Preferred Interview Time"
                name="interviewTime"
                type="datetime-local"
                value={values.interviewTime}
                onChange={handleChange}
                error={!!errors.interviewTime}
                helperText={errors.interviewTime}
                InputLabelProps={{
                  shrink: true,
                }}
              />

              {/* Remember Me Checkbox */}
              <FormControlLabel
                control={<Checkbox name="rememberMe" color="primary" />}
                label="Remember me"
                checked={values.rememberMe}
                onChange={handleChange}
              />

              {/* Submit Button */}
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
              >
                Submit
              </Button>

              {/* Forgot Password and Sign Up Links */}
              <Grid container>
                <Grid item xs>
                <Link href="#" variant="body2">
                    Forgot password?
                  </Link>
                </Grid>
                <Grid item>
                  <Link href="#" variant="body2">
                    {"Don't have an account? Sign Up"}
                  </Link>
                </Grid>
              </Grid>

              {/* Copyright */}
              <Box mt={5}>
                <Typography variant="body2" color="text.secondary" align="center">
                  {'Copyright © '}
                  <Link color="inherit" href="https://mui.com/">
                    Your Website
                  </Link>{' '}
                  {new Date().getFullYear()}
                  {'.'}
                </Typography>
              </Box>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
}
