import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Avatar from '@mui/material/Avatar';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import PersonIcon from '@mui/icons-material/Person';
import EmailIcon from '@mui/icons-material/Email';
import DisplaySettingsIcon from '@mui/icons-material/DisplaySettings';
import GroupIcon from '@mui/icons-material/Group';

import useFormState from '../hooks/useFormState';
import useFormValidation from '../hooks/useFormValidation';

const defaultTheme = createTheme();

const JobApplicationForm = () => {
  const [formData, handleChange, setFormData] = useFormState({
    fullName: '',
    email: '',
    phoneNumber: '',
    applyingForPosition: '',
    relevantExperience: '',
    portfolioURL: '',
    managementExperience: '',
    additionalSkills: [],
    preferredInterviewTime: '',
    attendingWithGuest: '',
    guestName: '',
  });

  const [touchedFields, setTouchedFields] = useState({
    fullName: false,
    email: false,
    phoneNumber: false,
    applyingForPosition: false,
    relevantExperience: false,
    portfolioURL: false,
    managementExperience: false,
    attendingWithGuest: false,
    guestName: false,
    preferredInterviewTime: false,
  });

  const navigate = useNavigate();
  const { errors, validate } = useFormValidation(formData, touchedFields);

  const handleSubmit = (event) => {
    event.preventDefault();

    // Mark all fields as touched
    setTouchedFields({
      fullName: true,
      email: true,
      phoneNumber: true,
      applyingForPosition: true,
      relevantExperience: true,
      portfolioURL: true,
      managementExperience: true,
      attendingWithGuest: true,
      guestName: true,
      preferredInterviewTime: true,
    });

    // Validate the form
    const newErrors = validate();

    if (Object.keys(newErrors).length === 0) {
      // Handle form submission logic (e.g., API call or further processing)
      console.log('Form data:', formData);
      alert('Form submitted successfully!');
    } else {
      alert('Please fix the errors in the form.');
    }
  };

  const backgroundImageUrl = 'https://picsum.photos/1200/800';

  return (
    <ThemeProvider theme={defaultTheme}>
      <div className="absolute inset-0 -z-10 h-full w-full items-center px-5 py-24 [background:radial-gradient(125%_125%_at_50%_10%,#000_40%,#63e_100%)]"></div>
      <Grid container component="main" sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: `url(${backgroundImageUrl})`,
            backgroundRepeat: 'no-repeat',
            backgroundColor: (t) =>
              t.palette.mode === 'light'
                ? t.palette.grey[50]
                : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Job Application
            </Typography>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="fullName"
                label="Full Name"
                name="fullName"
                autoComplete="fullName"
                value={formData.fullName}
                onChange={handleChange}
                error={touchedFields.fullName && !!errors.fullName}
                helperText={touchedFields.fullName && errors.fullName}
                autoFocus
                InputProps={{
                  startAdornment: (
                    <PersonIcon sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
                  ),
                }}
              />

              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                value={formData.email}
                onChange={handleChange}
                error={touchedFields.email && !!errors.email}
                helperText={touchedFields.email && errors.email}
                InputProps={{
                  startAdornment: (
                    <EmailIcon sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
                  ),
                }}
              />

              <TextField
                margin="normal"
                required
                fullWidth
                name="phoneNumber"
                label="Phone Number"
                type="tel"
                id="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
                error={touchedFields.phoneNumber && !!errors.phoneNumber}
                helperText={touchedFields.phoneNumber && errors.phoneNumber}
              />

              <FormControl fullWidth margin="normal" required>
                <InputLabel id="applyingForPosition-label">Applying for Position</InputLabel>
                <Select
                  labelId="applyingForPosition-label"
                  id="applyingForPosition"
                  name="applyingForPosition"
                  value={formData.applyingForPosition}
                  onChange={handleChange}
                  error={touchedFields.applyingForPosition && !!errors.applyingForPosition}
                >
                  <MenuItem value="Developer">Developer</MenuItem>
                  <MenuItem value="Designer">Designer</MenuItem>
                  <MenuItem value="Manager">Manager</MenuItem>
                </Select>
              </FormControl>

              {formData.applyingForPosition === 'Developer' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="relevantExperience"
                  label="Relevant Experience"
                  id="relevantExperience"
                  value={formData.relevantExperience}
                  onChange={handleChange}
                  error={touchedFields.relevantExperience && !!errors.relevantExperience}
                  helperText={touchedFields.relevantExperience && errors.relevantExperience}
                />
              )}

              {formData.applyingForPosition === 'Designer' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="portfolioURL"
                  label="Portfolio URL"
                  id="portfolioURL"
                  value={formData.portfolioURL}
                  onChange={handleChange}
                  error={touchedFields.portfolioURL && !!errors.portfolioURL}
                  helperText={touchedFields.portfolioURL && errors.portfolioURL}
                />
              )}

              {formData.applyingForPosition === 'Manager' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="managementExperience"
                  label="Management Experience"
                  id="managementExperience"
                  value={formData.managementExperience}
                  onChange={handleChange}
                  error={touchedFields.managementExperience && !!errors.managementExperience}
                  helperText={touchedFields.managementExperience && errors.managementExperience}
                />
              )}

              <FormControl fullWidth margin="normal" required>
                <InputLabel id="attendingWithGuest-label">Are you attending with a guest?</InputLabel>
                <Select
                  labelId="attendingWithGuest-label"
                  id="attendingWithGuest"
                  name="attendingWithGuest"
                  value={formData.attendingWithGuest}
                  onChange={handleChange}
                  error={touchedFields.attendingWithGuest && !!errors.attendingWithGuest}
                >
                  <MenuItem value="Yes">Yes</MenuItem>
                  <MenuItem value="No">No</MenuItem>
                </Select>
              </FormControl>

              {formData.attendingWithGuest === 'Yes' && (
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="guestName"
                  label="Guest Name"
                  id="guestName"
                  value={formData.guestName}
                  onChange={handleChange}
                  error={touchedFields.guestName && !!errors.guestName}
                  helperText={touchedFields.guestName && errors.guestName}
                />
              )}

              <TextField
                margin="normal"
                required
                fullWidth
                name="preferredInterviewTime"
                label="Preferred Interview Time"
                id="preferredInterviewTime"
                value={formData.preferredInterviewTime}
                onChange={handleChange}
                error={touchedFields.preferredInterviewTime && !!errors.preferredInterviewTime}
                helperText={touchedFields.preferredInterviewTime && errors.preferredInterviewTime}
              />

              <div
                className="relative px-5 py-3 overflow-hidden font-medium text-gray-600 bg-gray-100 border border-gray-100 rounded-lg shadow-inner group"
                style={{
                  marginTop: '20px',
                  textAlign: 'center',
                  position: 'relative',
                  zIndex: 1,
                }}
              >
                <span className="absolute top-0 left-0 w-0 h-0 transition-all duration-200 border-t-2 border-gray-600 group-hover:w-full ease"></span>
                <span className="absolute bottom-0 right-0 w-0 h-0 transition-all duration-200 border-b-2 border-gray-600 group-hover:w-full ease"></span>
                <span className="absolute top-0 left-0 w-full h-0 transition-all duration-300 delay-200 bg-gray-600 group-hover:h-full ease"></span>
                <span className="absolute bottom-0 left-0 w-full h-0 transition-all duration-300 delay-200 bg-gray-600 group-hover:h-full ease"></span>
                <span className="absolute inset-0 w-full h-full duration-300 delay-300 bg-gray-900 opacity-0 group-hover:opacity-100"></span>
                <span
                  className="relative transition-colors duration-300 delay-200 group-hover:text-white ease"
                  style={{ zIndex: 2 }}
                >
                  Submit Application
                </span>
              </div>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

export default JobApplicationForm;
